import 'package:flutter/material.dart';
import 'package:labouffe/screen/panier.dart';
import 'homepage.dart';

class SplashScreen extends StatelessWidget {
  const SplashScreen({Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    bool isDarkMode = false;
    return Directionality(
      textDirection: TextDirection.rtl,
      child: Scaffold(
        body: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            // Logo
            Expanded(
                  child: Container(
                    width: double.infinity,
                    height: MediaQuery.of(context).size.height * 0.3,
                    child: ClipRRect(
                      borderRadius: const BorderRadius.only(
                        bottomLeft: Radius.circular(40),
                        bottomRight: Radius.circular(40),
                      ),
                      child: Image.network(
                        'data:image/jpeg;base64,',
                        width: 200,
                        height: 200,
                        fit: BoxFit.fill,
                      ),
                    ),
                  ),

            ),
            const SizedBox(height: 70),
             ElevatedButton(
               onPressed: () {
                  Navigator.pushReplacement(
                    context,
                     MaterialPageRoute(builder: (context) => HomePage(isDarkMode: false,onThemeToggle: () { setState(() {  isDarkMode = !isDarkMode;});},
                    )),
                  );
               },
               style: ElevatedButton.styleFrom(
                 padding: const EdgeInsets.symmetric(horizontal: 40, vertical: 20),
                 shape: RoundedRectangleBorder(
                   borderRadius: BorderRadius.circular(10),
                 ),
               ),
               child: const Text(
                 'Commencer',
                 style: TextStyle(fontSize: 18),
               ),
             ),

            const SizedBox(height: 20),

            // Progress bar en Ã©toiles
            Row(
              mainAxisAlignment: MainAxisAlignment.center,
              children: List.generate(
                5,
                    (index) => Icon(
                  Icons.star,
                  color: index < 3 ? Colors.yellow : Colors.grey, // Exemple de remplissage
                  size: 30,
                ),
              ),
            ),

            const SizedBox(height: 20),


             Image.network(
             'data:image/jpeg;base64,',
               width: 100,
               height: 100,
             ),

            const SizedBox(height: 30), // Ajout d'un espace en bas
          ],
        ),
      ),
    );
  }

  setState(Null Function() param0) {}
}
